state.shader:
#version 150

uniform mat4 projection;
uniform mat4 modelview;

uniform mat4 bonematrix[20];

in vec4 position;
in vec3 normal;
in vec2 texcoord;
in int  boneindex;

out float light;

void main() {
	gl_Position = projection * modelview * bonematrix[boneindex] * position;
}


#version 330

out vec4 frag;

in float light;

void main() {
	frag = vec4(0.0,0.0,0.0,1.0);
}
