state.shader:
#version 150

uniform mat4 projection;
uniform mat4 modelview;

in vec4 position;
in vec3 normal;
in vec2 texcoord;

out vec2 tc;
out float light;

void main() {
	gl_Position = projection * modelview * position;
	tc = texcoord;
	light = (normal.z+normal.y)*0.25+0.5;
}


#version 330

uniform sampler2D tex;

in vec2 tc;
in float light;
out vec4 frag;

void main() { 
	vec4 t = texture2D(tex,tc)*vec4(light,light,light,1.0f);
	if (0.2 > t.w) discard;
	frag = t;
}
