state.shader:
#version 150

uniform mat4 projection;
uniform mat4 modelview;

in vec4 position;
in vec3 normal;
in vec2 texcoord;

out vec2 tc;

void main() {
	gl_Position = projection * modelview * position;
	tc = texcoord*vec2(2.0,1.0);
}


#version 330

uniform sampler2D tex;

in vec2 tc;
out vec4 frag;

void main() { 
	frag = texture2D(tex,tc);
}
